import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import PropTypes from 'prop-types';


Dich.propTypes = {
    ketqua: PropTypes.string,
    quote: PropTypes.string,
    cleartran: PropTypes.bool,
    load: PropTypes.bool,
}
Dich.defaultProps = {
    ketqua: '',
    quote: '',
    cleartran: null,
    load: null,
}

function Dich(props) {
    const{ quote, clear } = props; 
    const [ ketqua, setKetqua ] = useState('');
    const [ load, setLoad ] = useState(false);
    const [ cleartran, setCleartran ] = useState(clear);

    function handleChange() {
        console.log(load);
        const api = `https://api.mymemory.translated.net/get?q=${quote}&langpair=en|vi`;
        fetch(api)
        .then(response => response.json())
        .then(response => {
            const kq = response.responseData.translatedText;
            console.log(load);
            if (clear && load) {
            setKetqua(kq);
            } else {setKetqua('kq')};
        })
        .catch((error) => {
            console.log(error)
          })
    }
    useEffect(() => {
        handleChange()
    },[])
    useEffect(() => {
        return setLoad(true);
    })
        return (
            <div>
                <button className="trans-button" onClick={handleChange()}>Translate</button>
                <div className="quote-tran">{ketqua}</div>
            </div>
            )
}
export default Dich;
