import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import PropTypes from 'prop-types';


Dich.propTypes = {
     ketqua: PropTypes.number,
     quote: PropTypes.string,
     cleartran: PropTypes.bool,
     load: PropTypes.bool,
}
Dich.defaultProps = {
     ketqua: 0,
     quote: '',
     cleartran: null,
     load: null,
}

function Dich(props) {
    const{ quote, clear } = props; 
    const [ ketqua, setKetqua ] = useState(0);
    const [ load, setLoad ] = useState(false);
    const [ cleartran, setCleartran ] = useState(clear);

    function handleChange() {
        
         const api = `https://api.mymemory.translated.net/get?q=${quote}&langpair=en|vi`;
           fetch(api)
          .then(response => response.json())
          .then(response => {
            const kq = response.responseData.translatedText;
            setKetqua(kq);
          })
          .catch((error) => {
              console.log(error)
            })
     }
    useEffect(handleChange(),[])
        return (
            <div>
                <button className="trans-button" onClick={handleChange()}>Translate</button>
                <div className="quote-tran">{ketqua}</div>
            </div>
            )
}
export default Dich;
