import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import PropTypes from 'prop-types';


Dich.propTypes = {
     ketqua: PropTypes.string,
     quote: PropTypes.string,
     clear: PropTypes.bool,
}
Dich.defaultProps = {
     ketqua: '',
     quote: '',
     clear: null,
}

function Dich(props) {
    const { quote, clear } = props;
    var i = clear;
    const [ ketqua, setKetqua ] = useState('');

    function handleChange() {
        
         const api = `https://api.mymemory.translated.net/get?q=${quote}&langpair=en|vi`;
           fetch(api)
          .then(response => response.json())
          .then(response => {
            const kq = response.responseData.translatedText;
            i = i++;
            if (i != 0) {
            setKetqua(kq)};
            console.log(kq);
          })
          .catch((error) => {
              console.log(error)
            })
     }
    function resettrans() {
        setKetqua('');
     }
    useEffect(handleChange,[]);
    useEffect(() => {
      console.log(clear);
      if (clear == 0) {
        setKetqua('')}},[clear]
     );
  return (
            <div>
                <button className="trans-button" onClick={handleChange()}>Translate</button>
                <div className="quote-tran">{ketqua}</div>
            </div>
            )
}
export default Dich;
