import React, {Component} from 'react';

class Dich extends Component {
        constructor(props) {
          super(props);
          this.state = {
            ketqua : {},
            isLoading : false,
            quote : ''
          }
          this.handleChange = this.handleChange.bind(this)
        }
componentDidUpdate(prevProps, prevState){
    console.log(this.props.quote)
    if (prevProps.quote !== this.props.quote) {
        window.text= this.props.quote
    }
}
handleChange = () => {
    this.setState(
        {quote : window.text}
    )
}
componentDidMount() {
    console.log(this.state.quote)
    const api = `https://api.mymemory.translated.net/get?q=${this.state.quote}&langpair=en|vi`;
    fetch(api)
    .then(response => response.json())
    .then(response => {
        this.setState({
            ketqua : response
        })
        this.setState({
            isLoading : true
        })
    });
}
render() {
    if (this.state.isLoading) {
    return (
    <div>
        <button className="trans-button" onClick={this.handleChange}>Translate</button>
        <div className="quote-tran">{this.state.ketqua.responseData.translatedText}</div>
    </div>
    )
    } else {return <div></div>}
}
}
export default Dich;
