    console.log(this.props.y)
    if (prevProps.y !== this.props.y) {
        window.text= this.props.y
    }
    console.log(text)
}
componentDidMount() {
    const api = `https://api.mymemory.translated.net/get?q=${window.text}&langpair=en|vi`;
    console.log(window.text)
    fetch(api)
    .then(response => response.json())
    .then(response => {
        this.setState({
            ketqua : response
        })
        this.setState({
            isLoading : true
        })
    });
}
render() {
    if (this.state.isLoading) {
    return (
    <div>
        <div className="quote-tran">{this.state.ketqua.responseData.translatedText}</div>
    </div>
    )
    } else {return <div></div>}
}
}
export default Dich;
