import React, { Component } from 'react';
import './App.scss';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      responseData : [],
      isLoading : false
    }
    this.getrandomquote = this.getrandomquote.bind(this);
  }
  componentDidMount() {
    fetch("https://type.fit/api/quotes")
    .then(response => response.json())
    .then(data => { 
      this.setState({responseData : data})
      this.setState({isLoading : true})
    })
    .then(data => console.log(data))
    .catch((error) => {
      console.log(error)
    })
  }
  getrandomquote() {
    let rd = Math.floor(Math.random()*(this.state.responseData.length + 1));
    let {text: quote} = this.state.responseData[rd];
    let authorquote = '';
    quote = '\"'+ quote +'\"';
    if (this.state.responseData[rd].author === null) {
      authorquote = "Khuyết danh"
    } else {authorquote = '__'+ this.state.responseData[rd].author +'__'};
    const href_twitter = 'https://twitter.com/intent/tweet?text=' + quote + "\n" + authorquote;
  }
  render() {
    if (this.isLoading) { 
      let rd = Math.floor(Math.random()*(this.state.responseData.length + 1));
    let {text: quote} = this.state.responseData[rd];
    let authorquote = '';
    quote = '\"'+ quote +'\"';
    if (this.state.responseData[rd].author === null) {
      authorquote = "Khuyết danh"
    } else {authorquote = '__'+ this.state.responseData[rd].author +'__'};
    const href_twitter = 'https://twitter.com/intent/tweet?text=' + quote + "\n" + authorquote;
    return (
    <div>
      <div class="body-container">
      <div class="body-title">
          <h2>Random Quotes</h2>
      </div>
      <div class="getquote">
          <button class="getquote-button" onClick={this.getrandomquote}>New quote</button>
      </div>
      <div class="quote-container">
          <div class="quote">{quote}</div>
          <div class="author">{authorquote}</div>
          <button class="trans-button">Translate</button>
          <div class="quote-tran">Translated text</div>
          <div class="share">
              <button class="share-button">
                  <a class="twitter-share-button" href={href_twitter} data-size="large">
                      <i class="fa fa-twitter" aria-hidden="true"></i>&nbsp;Tweet</a>
              </button>
              <div class="fb-share-button" data-href="https://voz.vn/f/diem-bao.33/" data-layout="button" data-size="large"><a target="_blank" 
                  href="https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fvoz.vn%2Ff%2Fdiem-bao.33%2F&amp;src=sdkpreparse" 
                  class="fb-xfbml-parse-ignore">Chia sẻ</a></div>
          </div>
      </div>
      </div>
      <div id="fb-root"></div>
    </div>
    )} else {
      return <div></div>
    }
  }
}

export default App;
