import React from 'react';
import './App.scss';
import Dich from './components/Dich';
import { useState } from 'react';
import { useEffect } from 'react';


function App() {
  
  const [ responseData, setResponseData ] = useState([]);
  const [ isLoading, setIsLoading ] = useState(false);
  const [ clear, setClear ] = useState(false);
    
  useEffect(() => {
    function fetchapi() {
      fetch("https://type.fit/api/quotes")
        .then(response => response.json())
        .then(response => {
          const data = response;
          const random = Math.floor(Math.random() * (data.length + 1));
          setResponseData(data[random].text);
          
        })
        .catch((error) => {
          console.log(error)
        })}
        setIsLoading(true);
    fetchapi();
  },);
  console.log(responseData);
    

    const script = document.createElement("script");

    script.src = "https://connect.facebook.net/vi_VN/sdk.js#xfbml=1&version=v8.0&appId=522995571786911&autoLogAppEvents=1";
    script.async = true;

    document.body.appendChild(script);

  function getupdate() {
    setRd(random);
    setClear(true);
  }
  // function handleClick() {
  //   setResponseData(responseData[rd].text)
  // }
