import React from 'react';
import { useState } from 'react';
import { useEffect } from 'react';
import PropTypes from 'prop-types';


Dich.propTypes = {
    ketqua: PropTypes.object,
    isLoading: PropTypes.bool,
    quote: PropTypes.string,
    clear: PropTypes.bool,
}
Dich.defaultProps = {
    ketqua: null,
    isLoading: null,
    quote: '',
    clear: null,
}

function Dich(props) {
        
    const{ quote } = props; 
    const [ ketqua, setKetqua ] = useState({});
    const [isLoading, setIsLoading] = useState(false);

    function handleChange() {
        console.log(quote)
        const api = `https://api.mymemory.translated.net/get?q=${quote}&langpair=en|vi`;
        fetch(api)
        .then(response => response.json())
        .then(response => {
            setKetqua(response);
            setIsLoading(true);
        });
    }
    useEffect(() => {
        handleChange()
    },[])

    if (isLoading) {
        return (
            <div>
                <button className="trans-button" onClick={handleChange()}>Translate</button>
                <div className="quote-tran">{ketqua.responseData.translatedText}</div>
            </div>
            )
        } else {
            return (
            <div>
            <button className="trans-button" onClick={handleChange()}>Translate</button>
            </div>)
        }
}
export default Dich;
