import React, {Component} from 'react';

class Dich extends Component {
        constructor(props) {
          super(props);
          this.state = {
            ketqua : {},
            isLoading : false,
            cleartran : this.props.clear
          }
          this.handleChange = this.handleChange.bind(this)
        }
handleChange = () => {
    console.log(this.props.quote)
    const api = `https://api.mymemory.translated.net/get?q=${this.props.quote}&langpair=en|vi`;
    fetch(api)
    .then(response => response.json())
    .then(response => {
        this.setState({
            ketqua : response
        })
        this.setState({
            isLoading : true
        })
        this.setState({
            cleartran : true
        })
    });
}
componentDidUpdate() {
    console.log({cleartran})
}
render() {
    const {ketqua, isLoading, cleartran} = this.state
    if (isLoading&&cleartran) {
        return (
        <div>
            <button className="trans-button" onClick={this.handleChange}>Translate</button>
            <div className="quote-tran">{ketqua.responseData.translatedText}</div>
        </div>
        )
    } else {
        return (
        <div>
        <button className="trans-button" onClick={this.handleChange}>Translate</button>
        </div>)
    }
}
}
export default Dich;
