import React, { Component } from 'react';
import './App.scss';
import Dich from './components/Dich';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      responseData: [],
      isLoading: false,
      rd: 0,
      clear: false
    }
    this.getupdate = this.getupdate.bind(this);
  }
  componentDidMount() {
    fetch("https://type.fit/api/quotes")
      .then(response => response.json())
      .then(response => {
        this.setState({ responseData: response })
        this.setState({ isLoading: true })
        this.setState({ rd: Math.floor(Math.random() * (this.state.responseData.length + 1)) })
      })
      .catch((error) => {
        console.log(error)
      })
    console.log(this.state.responseData)

    const script = document.createElement("script");

    script.src = "https://connect.facebook.net/vi_VN/sdk.js#xfbml=1&version=v8.0&appId=522995571786911&autoLogAppEvents=1";
    script.async = true;

    document.body.appendChild(script);

  }
  getupdate() {
    this.setState({ rd: Math.floor(Math.random() * (this.state.responseData.length + 1)) })
    this.setState({ clear: true })
  }
  handleClick = () => {
    this.setState({
      x: this.state.responseData[this.state.rd].text
    })
  }
  render() {
    if (this.state.isLoading) {
      const { rd } = this.state;
      const quote = this.state.responseData[rd].text;
      let authorquote = '';
      if (this.state.responseData[rd].author === null) {
        authorquote = "Khuyết danh"
      } else { authorquote = '__' + this.state.responseData[rd].author + '__' };
      const href_twitter = 'https://twitter.com/intent/tweet?text=' + quote + "\n" + authorquote;
      return (
        <div>
          <div className="body-container">
            <div className="body-title">
              <h2>Random Quotes</h2>
            </div>
            <div className="getquote">
              <button className="getquote-button" onClick={this.getupdate}>New quote</button>
            </div>
            <div className="quote-container">
              <div className="quote">"{quote}"</div>
              <div className="author">{authorquote}</div>
              <Dich quote={quote} clear={this.state.clear} />
              <div className="share">
                <button className="share-button">
                  <a className="twitter-share-button" href={href_twitter} data-size="large">
                    <i className="fa fa-twitter" aria-hidden="true"></i>&nbsp;Tweet</a>
                </button>
                <div className="fb-share-button" data-href="#" data-layout="button" data-size="large"><a target="_blank" rel="noopener noreferrer"
                  href="https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fvoz.vn%2Ff%2Fdiem-bao.33%2F&amp;src=sdkpreparse"
                  className="fb-xfbml-parse-ignore">Chia sẻ</a></div>
              </div>
            </div>
          </div>
          <div id="fb-root"></div>
        </div>
      )
    } else {
      return <div></div>
    }
  }
}

export default App;
