import React, {Component} from 'react';

class Dich extends Component {
        constructor(props) {
          super(props);
          this.state = {
            ketqua : '',
            isLoading : false
          }
        }
componentDidMount() {
    const y = this.props.x;
    const api = `https://api.mymemory.translated.net/get?q=${y}&langpair=en|vi`;
    fetch(api)
    .then(response => {response.json()})
    .then(result => {
        this.setState({
            ketqua : result.translatedText
        })
        this.setState({
            isLoading : true
        })
    });
}
render() {
    if (this.state.isLoading) {
    return (
    <div>
        <div className="quote-tran">{this.state.ketqua}</div>
    </div>
    )
    } else {return <div></div>}
}
}
export default Dich;
